mapper.update:
  	
  	UPDATE jspuser
	   SET name = #{name} 
	     , pass = #{pass}
	     , addr1= #{addr1}
	     , addr2= #{addr2}
	     , zipcd= #{zipcd} 
	     , email= #{email}
	     , tel  = #{tel}
	     , birth= #{birth} 
	     , profile = #{profile,jdbcType=VARCHAR}
     WHERE userId = #{userId}
